import React, { useEffect, useState } from 'react';
import '../Css/Comingsoon.css';
import logo from '../images/logo-1.png'

const Commingsoon = () => {
  const [timeLeft, setTimeLeft] = useState({});

  const calculateTimeLeft = () => {
    const difference = +new Date().setHours(24, 0, 0, 0) - +new Date();
    let time = {};

    if (difference > 0) {
      time = {
        Days: Math.floor(difference / (1000 * 60 * 60 * 24)),
        Hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
        Minutes: Math.floor((difference / 1000 / 60) % 60),
        Seconds: Math.floor((difference / 1000) % 60),
      };
    }

    return time;
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft(calculateTimeLeft());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  return (
    <div className="coming-container">
      <img src={logo} alt="Vaan Vayal Logo" className="logo" />
      <h4 className="tagline">Something great is on the way</h4>
      <h1 className="main-title">COMING SOON</h1>

      <div className="timer-container">
        {Object.keys(timeLeft).map((key) => (
          <div className="time-box" key={key}>
            <h2>{timeLeft[key]}</h2>
            <p>{key}</p>
          </div>
        ))}
      </div>

    
    </div>
  );
};

export default Commingsoon;
